import React, { useState } from 'react';
import { View, Text, FlatList, TouchableOpacity, Alert, StyleSheet } from 'react-native';

const Cart = ({ route }) => {
  const { cartItems } = route.params; // Nhận dữ liệu giỏ hàng từ route
  const [items, setItems] = useState(cartItems); // Lưu giỏ hàng trong state để có thể cập nhật

  const removeItem = (itemToRemove) => {
    Alert.alert(
      'Xác nhận',
      `Bạn có chắc chắn muốn xóa ${itemToRemove.title} khỏi giỏ hàng?`,
      [
        {
          text: 'Hủy',
          style: 'cancel',
        },
        {
          text: 'Xóa',
          onPress: () => {
            setItems(prevItems => prevItems.filter(item => item.title !== itemToRemove.title)); // Cập nhật giỏ hàng
          },
        },
      ],
      { cancelable: true }
    );
  };

  const renderCartItem = ({ item }) => (
    <View style={styles.cartItem}>
      <View>
        <Text style={styles.itemTitle}>{item.title}</Text>
        <Text style={styles.itemPrice}>{item.price}</Text>
      </View>
      <TouchableOpacity style={styles.removeButton} onPress={() => removeItem(item)}>
        <Text style={styles.removeButtonText}>Xóa</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={{ flex: 1 }}>
      <Text style={styles.header}>Giỏ hàng</Text>
      <FlatList
        data={items} // Sử dụng items để hiển thị
        renderItem={renderCartItem}
        keyExtractor={(item) => item.title} // Hoặc một giá trị duy nhất khác
      />
      {/* Có thể thêm nút để xử lý thanh toán hoặc đặt hàng */}
    </View>
  );
};

const styles = StyleSheet.create({
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    padding: 10,
  },
  cartItem: {
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  itemTitle: {
    fontSize: 16,
    fontWeight: '600',
  },
  itemPrice: {
    fontSize: 14,
    color: '#555',
  },
  removeButton: {
    backgroundColor: 'red',
    borderRadius: 5,
    padding: 5,
  },
  removeButtonText: {
    color: 'white',
    fontWeight: 'bold',
  },
});

export default Cart;
