import { AntDesign } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import React, { useState, useEffect, useRef } from 'react';
import { Text, ScrollView, Image, View, FlatList, Dimensions, TouchableOpacity, TextInput } from 'react-native';
import Toast from 'react-native-toast-message';

const foodImages = [
  require('../assets/Steak.jpg'),
  require('../assets/Pizza.jpg'),
  require('../assets/crackBurger.jpg'),
  require('../assets/cream.jpg'),
  require('../assets/Ramen.jpg'),
];

const foodItems = [
  {
    image: require('../assets/crackBurger.jpg'),
    title: 'Healthy chicken protein burger',
    oldprice: '45.000 đ',
    price: '30.000 đ',
    description: '390 kcal',
    Evaluate: 5,
    quantity: 10,
  },
  {
    image: require('../assets/Pho.jpg'),
    title: 'Pho Hehehehehe',
    oldprice: '45.000 đ',
    price: '30.000 đ',
    description: '666 kcal',
    Evaluate: 5,
    quantity: 10,
  },
  {
    image: require('../assets/roll.jpg'),
    title: 'Vietnamese Rice Paper Rolls',
    oldprice: '45.000 đ',
    price: '30.000 đ',
    description: '333 kcal',
    Evaluate: 5,
    quantity: 10,
  },
];

const { width, height } = Dimensions.get('window');

const FoodItem = ({ item, toggleFavorite, addToCart, favorites, addedToCart, navigation }) => {
  const [quantity, setQuantity] = useState(1);

  return (
    <View style={{ marginTop: '5%' }}>
      <TouchableOpacity onPress={() => navigation.navigate('Details', { item })}>
        <Image
          source={item.image}
          style={{ width: width * 0.9, height: width * 0.36, marginHorizontal: width * 0.05, borderRadius: 10 }}
          resizeMode="cover"
        />
        <View style={{ paddingLeft: '5%', justifyContent: 'center' }}>
          <Text style={{ fontSize: 20, fontWeight: 'bold', marginLeft: '5%' }}>{item.title}</Text>
        </View>
        <View style={{ flexDirection: 'row', paddingLeft: '5%', justifyContent: 'center' }}>
          <Text style={{ textDecorationLine: 'line-through', fontSize: 18, fontWeight: '400', marginRight: '5%' }}>
            {item.oldprice}
          </Text>
          <Text style={{ marginLeft: '5%', fontWeight: 'bold', fontSize: 18 }}>{item.price}</Text>
          <TouchableOpacity onPress={() => toggleFavorite(item)}>
            <AntDesign
              name={favorites.includes(item) ? 'heart' : 'hearto'}
              color={favorites.includes(item) ? 'red' : 'grey'}
              size={24}
              style={{ marginLeft: 8 }}
            />
          </TouchableOpacity>
          <TextInput
            style={{ width: 40, height: 40, borderColor: 'gray', borderWidth: 1, marginLeft: 8 }}
            keyboardType="numeric"
            value={String(quantity)}
            onChangeText={(text) => {
              const value = Number(text);
              if (!isNaN(value) && value > 0) {
                setQuantity(value);
              }
            }}
          />
          <TouchableOpacity onPress={() => addToCart({ ...item, quantity })}>
            <AntDesign
              name='shoppingcart'
              size={24}
              style={{ marginLeft: 8, color: addedToCart[item.title] ? 'green' : 'black' }}
            />
          </TouchableOpacity>
        </View>
      </TouchableOpacity>
    </View>
  );
};

const Home = () => {
  const navigation = useNavigation();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [favorites, setFavorites] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [addedToCart, setAddedToCart] = useState({});
  
  const flatListRef = useRef(null);

  const handleCartPress = () => {
    navigation.navigate('Cart', { cartItems });
  };

  useEffect(() => {
    const interval = setInterval(() => {
      const nextIndex = (currentIndex + 1) % foodImages.length;
      setCurrentIndex(nextIndex);
      flatListRef.current?.scrollToIndex({ index: nextIndex, animated: true });
    }, 5000);

    return () => clearInterval(interval);
  }, [currentIndex]);

  const handleDotPress = (index) => {
    setCurrentIndex(index);
    flatListRef.current?.scrollToIndex({ index, animated: true });
  };

  const renderItem = ({ item }) => (
    <TouchableOpacity>
      <Image
        source={item}
        style={{
          width: width * 0.9,
          height: height * 0.25,
          marginHorizontal: width * 0.05,
          borderRadius: 10,
        }}
        resizeMode="cover"
      />
    </TouchableOpacity>
  );

  const toggleFavorite = (item) => {
    setFavorites((prevFavorites) => {
      if (prevFavorites.includes(item)) {
        return prevFavorites.filter(fav => fav !== item);
      } else {
        return [...prevFavorites, item];
      }
    });
  };

  const addToCart = (item) => {
    setCartItems((prevItems) => {
      const existingItem = prevItems.find(cartItem => cartItem.title === item.title);
      if (existingItem) {
        return prevItems.map(cartItem =>
          cartItem.title === item.title ? { ...cartItem, quantity: cartItem.quantity + item.quantity } : cartItem
        );
      } else {
        return [...prevItems, item];
      }
    });
    setAddedToCart((prev) => ({ ...prev, [item.title]: true }));
    Toast.show({
      text1: 'Đã thêm vào giỏ hàng',
      text2: `${item.title} x ${item.quantity} đã được thêm vào giỏ hàng!`,
      position: 'bottom',
      visibilityTime: 2000,
      autoHide: true,
    });
  };

  return (
    <View style={{ flex: 1 }}>
      <ScrollView style={{ flex: 1, paddingBottom: 88 }}>
        {/* Header */}
        <View style={{ flexDirection: 'row', alignItems: 'center', padding: 16, width: width }}>
          <TouchableOpacity>
            <Image source={require('../assets/logo_canteen.png')} style={{ width: width * 0.25, height: width * 0.25 }} />
          </TouchableOpacity>
          <TextInput
            style={{ width: '55%', height: width * 0.15, backgroundColor: '#ADD8E6', borderRadius: 8 }}
            placeholder="Bạn muốn gì?"
            right={<TextInput.Icon icon={() => (
              <TouchableOpacity onPress={() => console.log('Icon pressed!')}>
                <AntDesign name="search1" size={24} color="black" />
              </TouchableOpacity>
            )} />}
          />
          <TouchableOpacity onPress={handleCartPress}>
            <AntDesign name='shoppingcart' size={28} style={{ marginLeft: 8 }} />
          </TouchableOpacity>
        </View>

        {/* Image slider */}
        <FlatList
          data={foodImages}
          renderItem={renderItem}
          keyExtractor={(item, index) => index.toString()}
          horizontal
          pagingEnabled
          showsHorizontalScrollIndicator={false}
          scrollEnabled={false}
          ref={flatListRef}
          getItemLayout={(data, index) => ({ length: width * 0.9 + width * 0.1, offset: (width * 0.9 + width * 0.1) * index, index })}
        />

        {/* Navigation dots */}
        <View style={{ flexDirection: 'row', justifyContent: 'center', marginTop: -30 }}>
          {foodImages.map((_, index) => (
            <TouchableOpacity
              key={index}
              style={{
                height: 18,
                width: 18,
                borderRadius: 9,
                backgroundColor: currentIndex === index ? '#A52A2A' : '#D3D3D3',
                marginHorizontal: 5,
              }}
              onPress={() => handleDotPress(index)}
            />
          ))}
        </View>

        {/* Recommended Section */}
        <View style={{ marginTop: '5%' }}>
          <Text style={{ fontSize: 25, fontWeight: 'bold', marginLeft: '5%' }}>Đề xuất</Text>
          {foodItems.map((item, index) => (
            <FoodItem 
              key={index} 
              item={item} 
              toggleFavorite={toggleFavorite} 
              addToCart={addToCart} 
              favorites={favorites} 
              addedToCart={addedToCart}
              navigation={navigation} // truyền navigation vào đây
            />
          ))}
        </View>
      </ScrollView>
    </View>
  );
};

export default Home;
