import { AntDesign } from '@expo/vector-icons';
import React, { useState, useEffect } from 'react';
import { Text, ScrollView, Image, View, FlatList, Dimensions } from 'react-native';
import { TextInput } from 'react-native-paper';

const foodImages = [
  require('./assets/Steak.jpg'),
  require('./assets/Pizza.jpg'),
  require('./assets/crackBurger.jpg'),
  require('./assets/cream.jpg'),
  require('./assets/Ramen.jpg'),
];

const { width } = Dimensions.get('window'); // Get the screen width for full-width images

const YourApp = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Automatically switch images every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex(prevIndex => (prevIndex + 1) % foodImages.length);
    }, 5000); // 5 seconds

    return () => clearInterval(interval); // Clear interval on component unmount
  }, []);

  const renderItem = ({ item }) => (
    <Image source={item} style={{ width, height: 200 }} resizeMode="cover" />
  );

  return (
    <ScrollView style={{ flex: 1 }}>
      <View style={{ flex: 1, flexDirection: 'row', alignItems: 'center', padding: 16 }} >
        <Image source={require('./assets/logo_canteen.png')} style={{ width: 110, height: 110 }} />
        <TextInput 
          style={{ width: '60%', height: 56, backgroundColor: '#ADD8E6', borderRadius: 8 }} 
          placeholder="What do you want?" 
          right={<TextInput.Icon icon="magnify" />} 
        />
        <AntDesign name='shoppingcart' size={28} style={{ marginLeft: 8 }} />
      </View>

      {/* Image Slider */}
      <FlatList
        data={foodImages}
        renderItem={renderItem}
        keyExtractor={(item, index) => index.toString()}
        horizontal
        pagingEnabled
        showsHorizontalScrollIndicator={false}
        scrollEnabled={false} // Disable manual scrolling since we auto-scroll
        style={{ marginTop: 16 }}
        contentOffset={{ x: currentIndex * width, y: 0 }} // Automatically scroll to the correct image
      />
    </ScrollView>
  );
};

export default YourApp;
