import React from 'react';
import { View, Text, Image, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';

const Details = ({ route }) => {
    const navigation = useNavigation();
    const { item } = route.params; // Lấy item từ params
  
    return (
       <View style={{flex: 1}}>
        <ScrollView style={styles.container}>
            <View style={styles.imageContainer}>
                <Image source={item.image} style={styles.image} resizeMode="cover" />
                <TouchableOpacity 
                    style={styles.cartButton} 
                    onPress={() => console.log('Added to cart!')}
                >
                    <AntDesign name="shoppingcart" size={24} color="white" />
                </TouchableOpacity>
            </View>
            <View style={styles.infoContainer}>
                <Text style={styles.title}>{item.title}</Text>
                <Text style={styles.description}>{item.description}</Text>
                <View style={{flexDirection: 'row',justifyContent:'space-around'}}>
                    <Text style={styles.oldprice}>{item.oldprice}</Text>
                    <Text style={styles.price}>{item.price}</Text>
                </View>
                
                <TouchableOpacity 
                    style={styles.orderButton} 
                    onPress={() => console.log('Order placed!')}
                >
                    <Text style={styles.orderButtonText}>Order Now</Text>
                </TouchableOpacity>
            </View>
        </ScrollView>
      </View>
    );
  };

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  imageContainer: {
    position: 'relative', // Đặt vị trí tương đối cho khung hình ảnh
  },
  image: {
    width: '100%',
    height: 300,
  },
  cartButton: {
    position: 'absolute', // Đặt vị trí tuyệt đối
    top: 20, // Khoảng cách từ đỉnh hình ảnh
    right: 20, // Khoảng cách từ bên phải
    backgroundColor: 'rgba(0, 0, 0, 0.6)', // Nền tối để dễ nhìn
    borderRadius: 20,
    padding: 10,
  },
  infoContainer: {
    padding: 20,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
  },
  description: {
    fontSize: 16,
    marginVertical: 10,
  },
  price: {
    fontSize: 22,
    fontWeight: 'bold',
    marginVertical: 10,
  },
  oldprice: {
    fontSize: 22,
    fontWeight: '400',
    marginVertical: 10,
    textDecorationLine: 'line-through',
  },
  orderButton: {
    backgroundColor: '#A52A2A',
    padding: 15,
    borderRadius: 5,
    alignItems: 'center',
  },
  orderButtonText: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  backButton: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 10,
    marginTop: 10,
  },
  backButtonText: {
    marginLeft: 8,
    fontSize: 16,
  },
});

export default Details;
