import React, { useState } from 'react';
import { View, Text, FlatList, TouchableOpacity, Alert } from 'react-native';

const Cart = ({ route }) => {
  const { cartItems } = route.params; // Nhận dữ liệu giỏ hàng từ route
  const [items, setItems] = useState(cartItems); // Lưu giỏ hàng trong state để có thể cập nhật

  const removeItem = (itemToRemove) => {
    Alert.alert(
      'Xác nhận',
      `Bạn có chắc chắn muốn xóa ${itemToRemove.title} khỏi giỏ hàng?`,
      [
        {
          text: 'Hủy',
          style: 'cancel',
        },
        {
          text: 'Xóa',
          onPress: () => {
            setItems(prevItems => prevItems.filter(item => item.title !== itemToRemove.title)); // Cập nhật giỏ hàng
          },
        },
      ],
      { cancelable: true }
    );
  };

  const renderCartItem = ({ item }) => (
    <View style={{ padding: 10, borderBottomWidth: 1, borderBottomColor: '#ccc', flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
      <View>
        <Text>{item.title}</Text>
        <Text>{item.price}</Text>
      </View>
      <TouchableOpacity onPress={() => removeItem(item)}>
        <Text style={{ color: 'red' }}>Xóa</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={{ flex: 1 }}>
      <Text style={{ fontSize: 24, fontWeight: 'bold', padding: 10 }}>Giỏ hàng</Text>
      <FlatList
        data={items} // Sử dụng items để hiển thị
        renderItem={renderCartItem}
        keyExtractor={(item) => item.title} // Hoặc một giá trị duy nhất khác
      />
      {/* Có thể thêm nút để xử lý thanh toán hoặc đặt hàng */}
    </View>
  );
};

export default Cart;
