const Details = ({ route, navigation }) => {
    const { item, favorites: initialFavorites } = route.params; // Nhận danh sách yêu thích từ Home
    const [favorites, setFavorites] = useState(initialFavorites); // Sử dụng state để theo dõi danh sách yêu thích

    const toggleFavorite = (id) => {
        let updatedFavorites;
        if (favorites.includes(id)) {
            // Nếu đã có trong danh sách yêu thích, xóa nó
            updatedFavorites = favorites.filter(favId => favId !== id);
        } else {
            // Nếu chưa có, thêm vào danh sách yêu thích
            updatedFavorites = [...favorites, id];
        }
        setFavorites(updatedFavorites);
        // Cập nhật favorites trong Home thông qua route.params
        navigation.setParams({ favorites: updatedFavorites });
    };

    return (
        <View style={{ flex: 1 }}>
            <ScrollView style={styles.container}>
                <View style={styles.imageContainer}>
                    <Image source={item.image} style={styles.image} resizeMode="cover" />
                    <View style={styles.overlay}>
                        <Text style={styles.title}>{item.title}</Text>
                        <View style={styles.ratingContainer}>
                            <Text style={styles.evaluate}>{item.Evaluate}</Text>
                            <AntDesign name='star' color='yellow' size={24} />
                        </View>
                        <Text style={styles.description}>{item.description}</Text>
                        <View style={{ flexDirection: 'row', justifyContent: 'space-between' }}>
                            <Text style={styles.oldprice}>{item.oldprice}</Text>
                            <Text style={styles.price}>{item.price}</Text>
                        </View>
                    </View>
                    <TouchableOpacity
                        style={styles.addToCartButton}
                        onPress={() => console.log('Added to Cart')}
                    >
                        <AntDesign name='shoppingcart' size={24} color='#fff' />
                    </TouchableOpacity>
                </View>

                {/* Nút yêu thích */}
                <TouchableOpacity
                    style={styles.orderButton}
                    onPress={() => toggleFavorite(item.id)} // Thêm hoặc xóa khỏi danh sách yêu thích
                >
                    <Text style={styles.orderButtonText}>
                        {favorites.includes(item.id) ? 'Remove from Favorites' : 'Add to Favorites'}
                    </Text>
                    <MaterialCommunityIcons
                        style={{ marginLeft: '5%' }}
                        name='silverware-fork-knife'
                        size={30}
                        color='white'
                    />
                </TouchableOpacity>

                <Text style={styles.recommendedHeader}>More</Text>
                <FlatList
                    data={recommendedItems}
                    renderItem={renderRecommendedItem}
                    keyExtractor={item => item.id.toString()}
                    horizontal={false}
                    showsHorizontalScrollIndicator={false}
                    contentContainerStyle={styles.recommendedList}
                />
            </ScrollView>

            {/* Footer Menu */}
            <View style={styles.footer}>
                <View style={styles.footerContent}>
                    <TouchableOpacity style={styles.footerButton} onPress={() => navigation.navigate('Favorites', { favorites })}>
                        <MaterialCommunityIcons name='silverware-fork-knife' size={30} color='#A52A2A' />
                        <Text style={styles.footerButtonText}>Favorites</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.footerButton} onPress={() => navigation.navigate('Home', { favorites })}>
                        <AntDesign name='home' size={28} color='#A52A2A' />
                        <Text style={styles.footerButtonText}>Home</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.footerButton}>
                        <EvilIcons name='bell' size={28} color='#A52A2A' />
                        <Text style={styles.footerButtonText}>Notification</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.footerButton}>
                        <AntDesign name='user' size={28} color='#A52A2A' />
                        <Text style={styles.footerButtonText}>Profile</Text>
                    </TouchableOpacity>
                </View>
            </View>
        </View>
    );
};
