import React from 'react';
import { View, Text, FlatList, TouchableOpacity } from 'react-native';

const Cart = ({ route }) => {
  const { cartItems } = route.params; // Nhận dữ liệu giỏ hàng từ route

  const renderCartItem = ({ item }) => (
    <View style={{ padding: 10, borderBottomWidth: 1, borderBottomColor: '#ccc' }}>
      <Text>{item.title}</Text>
      <Text>{item.price}</Text>
    </View>
  );

  return (
    <View style={{ flex: 1 }}>
      <Text style={{ fontSize: 24, fontWeight: 'bold', padding: 10 }}>Giỏ hàng</Text>
      <FlatList
        data={cartItems}
        renderItem={renderCartItem}
        keyExtractor={(item) => item.title} // Hoặc một giá trị duy nhất khác
      />
      {/* Có thể thêm nút để xử lý thanh toán hoặc đặt hàng */}
    </View>
  );
};

export default Cart;
