import { AntDesign } from '@expo/vector-icons';
import React, { useState, useEffect } from 'react';
import { Text, ScrollView, Image, View, FlatList, Dimensions, TouchableOpacity } from 'react-native';
import { TextInput } from 'react-native-paper';

const foodImages = [
  require('./assets/Steak.jpg'),
  require('./assets/Pizza.jpg'),
  require('./assets/crackBurger.jpg'),
  require('./assets/cream.jpg'),
  require('./assets/Ramen.jpg'),
];

const { width } = Dimensions.get('window'); // Lấy chiều rộng màn hình để hiển thị hình ảnh toàn màn hình

const YourApp = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Tự động chuyển hình ảnh mỗi 5 giây
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex(prevIndex => (prevIndex + 1) % foodImages.length);
    }, 5000); // 5 giây

    return () => clearInterval(interval); // Xóa bộ đếm thời gian khi thành phần bị huỷ
  }, []);

  // Hiển thị từng hình ảnh trong thanh trượt
  const renderItem = ({ item }) => (
    <Image source={item} style={{ width, height: 200 }} resizeMode="cover" />
  );

  // Xử lý sự kiện khi nhấn vào các chấm để chuyển hình ảnh
  const handleDotPress = (index) => {
    setCurrentIndex(index);
  };

  return (
    <ScrollView style={{ flex: 1 }}>
      {/* Phần đầu */}
      <View style={{ flex: 1, flexDirection: 'row', alignItems: 'center', padding: 16 }} >
        <Image source={require('./assets/logo_canteen.png')} style={{ width: 110, height: 110 }} />
        <TextInput 
          style={{ width: '60%', height: 56, backgroundColor: '#ADD8E6', borderRadius: 8 }} 
          placeholder="Bạn muốn ăn gì?" 
          right={<TextInput.Icon icon="magnify" />} 
        />
        <AntDesign name='shoppingcart' size={28} style={{ marginLeft: 8 }} />
      </View>

      {/* Thanh trượt hình ảnh */}
      <FlatList
        data={foodImages}
        renderItem={renderItem}
        keyExtractor={(item, index) => index.toString()}
        horizontal
        pagingEnabled
        showsHorizontalScrollIndicator={false}
        scrollEnabled={false} // Vô hiệu hóa cuộn tay vì chúng ta đã có tự động chuyển
        style={{ marginTop: 16 }}
        contentOffset={{ x: currentIndex * width, y: 0 }} // Tự động chuyển sang hình ảnh đúng
      />

      {/* Các chấm điều hướng dưới thanh trượt */}
      <View style={{ flexDirection: 'row', justifyContent: 'center', marginTop: 16 }}>
        {foodImages.map((_, index) => (
          <TouchableOpacity
            key={index}
            style={{
              height: 10,
              width: 10,
              borderRadius: 5,
              backgroundColor: currentIndex === index ? '#333' : '#ccc', // Chấm đang hoạt động có màu đậm hơn
              marginHorizontal: 6,
            }}
            onPress={() => handleDotPress(index)} // Chuyển hình ảnh khi chạm vào chấm
          />
        ))}
      </View>
    </ScrollView>
  );
};

export default YourApp;
