import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View,img} from 'react-native';
import logo from './assets/logo.png';
import { TextInput,Button } from "react-native-paper";
export default function App() {
  return (
    <View style={styles.container}>
      <img src={logo}></img>

      <View style={styles.TextInput}>
        <TextInput left={<TextInput.Icon icon="email"/>} placeholder='Email'></TextInput>
        <TextInput left={<TextInput.Icon icon="lock"/>} placeholder='Mật Khẩu'></TextInput>
      </View>

      <View style={styles.Button}>
        <Button title='Đăng Nhập' color='#4682b4' ></Button>
      </View>

      <View>
        <Button color='#f5f5f5'></Button>
      </View>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  TextInput: {
    flex :1,
  },
  Button: {
    flex: 1,
  }

});
